import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import TimeIcon from "@material-ui/icons/AccessTimeRounded";
import PlaceIcon from "@material-ui/icons/PlaceRounded";
import getHost from "../utils/getHost";

interface Props {
  name: string;
  imageCover: string;
}

const useStyles = makeStyles(theme => ({
  Cover: {
    display: "flex",
    flexDirection: "column"
  },
  imageCover: {
    position: "relative",
    minHeight: "40rem",
    // minHeight: "100%",
    backgroundAttachment: "fixed",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover"
  },
  titleContainer: {
    position: "absolute",
    // top: "30%",
    textAlign: "center",
    color: "#000",
    // left: "0",
    // right: "0",
    marginLeft: "auto",
    marginRight: "auto",
    width: "fit-content",
    padding: "3rem",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "rgba(255,255,255,0.7)",
    boxShadow:
      "rgba(255, 255, 255, 0.2) 0px 2px 4px -1px, rgba(255, 255, 255, 0.14) 0px 4px 5px 0px, rgba(255, 255, 255, 0.12) 0px 1px 10px 0px"
  },
  title: {
    display: "flex",
    justifyContent: "center"
  },
  details: {
    display: "flex",
    justifyContent: "center"
  },
  time: {
    display: "flex",
    justifyContent: "center"
  },
  place: {
    display: "flex",
    justifyContent: "center"
  }
}));

// image={`${getHost()}/img/tours/${imageCover}`}
const Cover: React.FC<Props> = ({ name, imageCover }) => {
  const classes = useStyles();

  return (
    <div className={classes.Cover}>
      <div
        className={classes.imageCover}
        style={{
          backgroundImage: `url("${getHost()}/img/tours/${imageCover}")`
        }}
      >
        <div className={classes.titleContainer}>
          <div className={classes.title}>
            <Typography variant="h1" component="h2" gutterBottom>
              {name}
            </Typography>
          </div>
          <div className={classes.details}>
            <div className={classes.time}>
              <TimeIcon />
              <Typography variant="h4" gutterBottom>
                7 days
              </Typography>
            </div>
            <div className={classes.place}>
              <PlaceIcon />
              <Typography variant="h4" gutterBottom>
                Miami, USA
              </Typography>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cover;
