import React from "react";
import Slider from "react-slick";

interface Props {}

const Images: React.FC<Props> = () => {
  // const sliderEl = React.useRef(null);
  let sliderEl = null;

  const play = () => {
    sliderEl.slickPlay();
  };
  const pause = () => {
    sliderEl.slickPause();
  };

  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000
  };
  return (
    <div>
      <Slider ref={slider => (sliderEl = slider)} {...settings}>
        <div>
          <h3>1</h3>
        </div>
        <div>
          <h3>2</h3>
        </div>
        <div>
          <h3>3</h3>
        </div>
        <div>
          <h3>4</h3>
        </div>
        <div>
          <h3>5</h3>
        </div>
        <div>
          <h3>6</h3>
        </div>
      </Slider>
      <div style={{ textAlign: "center" }}>
        <button className="button" onClick={play}>
          Play
        </button>
        <button className="button" onClick={pause}>
          Pause
        </button>
      </div>
    </div>
  );
};

export default Images;
