import getHost from "./getHost";

const ROLES = {
  user: "Regular User",
  guide: "Tour Guide",
  lead_guide: "Lead Guide",
  admin: "Admin User"
};

export const parseRole = (role: string): string => ROLES[role];

export const imgUrl = (type, name) => `${getHost()}/img/${type}/${name}`;
