import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import TimeIcon from "@material-ui/icons/AccessTimeRounded";
import PlaceIcon from "@material-ui/icons/PlaceRounded";
import getHost from "../utils/getHost";
import { colors } from "../utils/styles";
import { StartLocation } from "../home/TourCard";
import SectionTitle from "./SectionTitle";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import InboxIcon from "@material-ui/icons/Inbox";
import DraftsIcon from "@material-ui/icons/Drafts";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";

interface Props {}

const useStyles = makeStyles(theme => ({
  QuickFacts: {},
  factsContainer: {},
  factItem: {}
}));

function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}

const QuickFacts: React.FC<Props> = ({}) => {
  const classes = useStyles();

  return (
    <div className={classes.QuickFacts}>
      <div className={classes.factsContainer}>
        <SectionTitle title="QUICK FACTS" />
        <div className={classes.factItem}>
          <List dense={true}>
            <ListItem>
              <ListItemIcon>
                <TimeIcon />
              </ListItemIcon>
              <ListItemText primary="NEXT DATE" />
              <ListItemSecondaryAction>
                <p>June 2021</p>
              </ListItemSecondaryAction>
            </ListItem>
            <ListItem>
              <ListItemIcon>
                <TimeIcon />
              </ListItemIcon>
              <ListItemText primary="DIFFICULTY" />
              <ListItemSecondaryAction>
                <p>Medium</p>
              </ListItemSecondaryAction>
            </ListItem>
            <ListItem>
              <ListItemIcon>
                <TimeIcon />
              </ListItemIcon>
              <ListItemText primary="PARTICIPANTS" />
              <ListItemSecondaryAction>
                <p>15 People</p>
              </ListItemSecondaryAction>
            </ListItem>
            <ListItem>
              <ListItemIcon>
                <TimeIcon />
              </ListItemIcon>
              <ListItemText primary="RATING" />
              <ListItemSecondaryAction>
                <p>4.8 / 5</p>
              </ListItemSecondaryAction>
            </ListItem>
          </List>
        </div>
      </div>
    </div>
  );
};

export default QuickFacts;

