import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import PlaceIcon from "@material-ui/icons/Place";
import CalendarIcon from "@material-ui/icons/CalendarToday";
import FlagIcon from "@material-ui/icons/EmojiFlagsRounded";
import PeopleIcon from "@material-ui/icons/PeopleOutlineRounded";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import TourCard from "./TourCard";

interface Props {
  date: string;
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  }
}));

const icons = {
  place: <PlaceIcon />,
  date: <CalendarIcon />,
  stops: <FlagIcon />,
  people: <PeopleIcon />
};

const SpotlightItem = ({ type, date }) => {
  return (
    <>
      <div className="icon-container">{icons[type]}</div>
      <div className="info-container">{date}</div>
    </>
  );
};

const Spotlights: React.FC<Props> = props => {
  const classes = useStyles();

  const spotLights = [
    <Grid key={1} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem type="place" {...props} />
    </Grid>,
    <Grid key={2} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem type="date" {...props} />
    </Grid>,
    <Grid key={3} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem type="stops" {...props} />
    </Grid>,
    <Grid key={4} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem type="people" {...props} />
    </Grid>
  ];

  return (
    <Grid container spacing={1}>
      {spotLights}
    </Grid>
  );
};

export default Spotlights;
