import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import TimeIcon from "@material-ui/icons/AccessTimeRounded";
import PlaceIcon from "@material-ui/icons/PlaceRounded";
import getHost from "../utils/getHost";
import { colors } from "../utils/styles";
import { StartLocation } from "../home/TourCard";
import SectionTitle from "./SectionTitle";

interface Props {}

const useStyles = makeStyles(theme => ({
  QuickFacts: {},
  factsContainer: {},
  factItem: {
    display: "flex",
    alignItems: "center"
  },
  icon: {
    marginRight: "1rem",
    "& svg": {
      color: colors.mainBlue,
      width: "2.5rem",
      height: "2.5rem"
    }
  },
  heading: {
    marginRight: "2rem",
    fontSize: "1.6rem"
  },
  info: {
    fontSize: "1.6rem"
  }
}));

const QuickFacts: React.FC<Props> = ({}) => {
  const classes = useStyles();

  return (
    <div className={classes.QuickFacts}>
      <div className={classes.factsContainer}>
        <SectionTitle title="QUICK FACTS" />
        <div className={classes.factItem}>
          <div className={classes.icon}>
            <TimeIcon />
          </div>
          <div className={classes.heading}>NEXT DATE</div>
          <div className={classes.info}>June 2021</div>
        </div>
      </div>
    </div>
  );
};

export default QuickFacts;

