import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import { TourFragment } from "../home/TourCards";
import { makeStyles } from "@material-ui/core/styles";
import Cover from "./Cover";
import QuickFacts from "./QuickFacts";
import TourGuides from "./TourGuides";
import About from "./About";
import Images from "./Images";
import Map from "./Map";
import Reviews from "./Reviews";
import Prompt from "./Prompt";

interface Props {
  slug: string | string[];
}

const TOUR = gql`
  query TOUR($slug: String!) {
    getTour(slug: $slug) {
      ...TourFragment
    }
  }
  ${TourFragment}
`;

const useStyles = makeStyles(theme => ({
  Tour: {},
  infoSection: {
    display: "flex"
  },
  factsAndGuides: {
    display: "flex"
  }
}));

const Tour: React.FC<Props> = ({ slug }) => {
  const { loading, error, data } = useQuery(TOUR, { variables: { slug } });
  const classes = useStyles();
  console.log("Toud data = ", data);

  if (loading) return <p>Loading ...</p>;

  if (data && data.getTour) {
    const {
      name,
      price,
      createdAt,
      summary,
      imageCover,
      difficulty,
      duration,
      startDates,
      startLocation,
      description,
      locations,
      maxGroupSize,
      rating,
      ratingsQuantity,
      ratingsAverage
    } = data.getTour;

    return (
      <main className={classes.Tour}>
        <Cover imageCover={imageCover} name={name} />
        <div className={classes.infoSection}>
          <div className={classes.factsAndGuides}>
            <QuickFacts />
            <TourGuides />
          </div>
          <About />
        </div>
        <Images />
        <Map />
        <Reviews />
        <Prompt />
      </main>
    );
  }
};

export default Tour;
