import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import PlaceIcon from "@material-ui/icons/Place";
import CalendarIcon from "@material-ui/icons/CalendarToday";
import FlagIcon from "@material-ui/icons/EmojiFlagsRounded";
import PeopleIcon from "@material-ui/icons/PeopleOutlineRounded";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import TourCard from "./TourCard";
import { colors } from "../utils/styles";
import formatMoney from "../utils/formatMoney";

interface Props {
  date: string;
  place: string;
  stops: number;
  people: number;
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    margin: "1rem 2rem"
  },
  spotlightItem: {
    display: "flex"
  },
  icon: {
    marginRight: "1rem",
    "& svg": {
      width: "2rem",
      height: "2rem",
      color: colors.mainBlue
    }
  },
  text: {
    display: "flex",
    alignItems: "center",
    height: "2rem",
    fontSize: "1rem"
  },
  details: {},
  price: {},
  rating: {}
}));

const icons = {
  place: <PlaceIcon />,
  date: <CalendarIcon />,
  stops: <FlagIcon />,
  people: <PeopleIcon />
};

const SpotlightItem = ({ icon, text }) => {
  const classes = useStyles();

  return (
    <Grid
      key={1}
      item
      xs={6}
      /*md={6} lg={4}*/ className={classes.spotlightItem}
    >
      <div className={classes.icon}>{icon}</div>
      <div className={classes.text}>{text}</div>
    </Grid>
  );
};

const Spotlights: React.FC<Props> = ({ date, place, stops, people }) => {
  const classes = useStyles();

  const infoContent = {
    place: {
      icon: <PlaceIcon />,
      text: place
    },
    date: {
      icon: <CalendarIcon />,
      text: date
    },
    stops: {
      icon: <FlagIcon />,
      text: `${stops} stop${stops === 1 ? "" : "s"}`
    },
    people: {
      icon: <PeopleIcon />,
      text: `${people} ${people === 1 ? "person" : "people"}`
    }
  };

  const spotLights = [
    <SpotlightItem {...infoContent.place} />,
    <SpotlightItem {...infoContent.date} />,
    <SpotlightItem {...infoContent.stops} />,
    <SpotlightItem {...infoContent.people} />
  ];

  return (
    <div className={classes.root}>
      <Grid container spacing={2}>
        {spotLights}
      </Grid>
      <div className={classes.details}>
        <div className={classes.price}>{formatMoney(34201)}</div>
        <div className={classes.rating}></div>
      </div>
    </div>
  );
};

export default Spotlights;
