import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import PlaceIcon from "@material-ui/icons/Place";
import CalendarIcon from "@material-ui/icons/CalendarToday";
import FlagIcon from "@material-ui/icons/EmojiFlagsRounded";
import PeopleIcon from "@material-ui/icons/PeopleOutlineRounded";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import TourCard from "./TourCard";

interface Props {
  date: string;
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    margin: "2rem"
  },
  spotlightItem: {
    display: "flex"
  },
  icon: {
    marginRight: "1rem",
    "& svg": {
      width: "2rem",
      height: "2rem"
    }
  },
  text: {
    height: "2rem",
    fontSize: "1rem"
  }
}));

const icons = {
  place: <PlaceIcon />,
  date: <CalendarIcon />,
  stops: <FlagIcon />,
  people: <PeopleIcon />
};

const SpotlightItem = ({ icon, text }) => {
  const classes = useStyles();

  return (
    <Grid
      key={1}
      item
      xs={6}
      /*md={6} lg={4}*/ className={classes.spotlightItem}
    >
      <div className={classes.icon}>{icon}</div>
      <div className={classes.text}>{text}</div>
    </Grid>
  );
};

const Spotlights: React.FC<Props> = ({ date }) => {
  const classes = useStyles();

  const infoContent = {
    place: {
      icon: <PlaceIcon />,
      text: "place"
    },
    date: {
      icon: <CalendarIcon />,
      text: date
    },
    stops: {
      icon: <FlagIcon />,
      text: "stops"
    },
    people: {
      icon: <PeopleIcon />,
      text: "people"
    }
  };

  const spotLights = [
    <SpotlightItem {...infoContent.place} />,
    <SpotlightItem {...infoContent.date} />,
    <SpotlightItem {...infoContent.stops} />,
    <SpotlightItem {...infoContent.people} />
  ];

  return (
    <div className={classes.root}>
      <Grid container spacing={1}>
        {spotLights}
      </Grid>
    </div>
  );
};

export default Spotlights;
