import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardActionArea from "@material-ui/core/CardActionArea";
import moment from "moment";
import colorPerLetter from "../utils/colorPerLetter";
import getHost from "../utils/getHost";
import Spotlights from "./Spotlights";
import { colors } from "../utils/styles";
import CardCollapsible from "./CardCollapsible";
import Difficulty from "./Difficulty";

export interface StartLocation {
  description: string;
  address: string;
  type: string;
  coordinates: [number];
}

export interface Location {
  _id: string;
  description: string;
  type: string;
  day: number;
  coordinates: [number];
}

interface Props {
  name: string;
  createdAt: number;
  summary: string;
  imageCover: string;
  duration: number;
  difficulty: string;
  startDates: [string];
  startLocation: StartLocation;
  locations: [Location];
  maxGroupSize: number;
}

const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: 345
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest
    })
  },
  expandOpen: {
    transform: "rotate(180deg)"
  },
  avatar: {
    backgroundColor: red[500]
  },
  summary: {
    fontSize: "1rem",
    color: colors.mainBlack
  }
}));

const TourCard: React.FC<Props> = props => {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const {
    name,
    createdAt,
    summary,
    imageCover,
    difficulty,
    duration,
    startDates,
    startLocation,
    locations,
    maxGroupSize
  } = props;

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const firstLetter = name
    .toLowerCase()
    .split("the ")[1][0]
    .toUpperCase();

  const closestTourDate = moment(closestDate(startDates)).format("MMMM YYYY");
  const place =
    startLocation && startLocation.description ? startLocation.description : "";
  const stops = locations ? locations.length : 0;

  return (
    <Grid item xs={12} md={6} lg={4}>
      <Card className={classes.root}>
        <CardHeader
          avatar={
            <Avatar
              aria-label="recipe"
              className={classes.avatar}
              style={{ backgroundColor: colorPerLetter(firstLetter) }}
            >
              {firstLetter}
            </Avatar>
          }
          action={
            <IconButton aria-label="settings">
              <MoreVertIcon />
            </IconButton>
          }
          title={name}
          subheader={moment(createdAt * 1).format("MMMM Do YYYY")}
        />
        <CardActionArea>
          <CardMedia
            className={classes.media}
            image={`${getHost()}/img/tours/${imageCover}`}
            title="Paella dish"
          />
          <CardContent>
            <Difficulty difficulty={difficulty} duration={duration} />
            <Typography
              variant="body2"
              color="textSecondary"
              component="p"
              className={classes.summary}
            >
              {summary}
            </Typography>
          </CardContent>
        </CardActionArea>
        <Spotlights
          date={closestTourDate}
          place={place}
          stops={stops}
          people={maxGroupSize}
        />
        <CardActions disableSpacing>
          <IconButton aria-label="add to favorites">
            <FavoriteIcon />
          </IconButton>
          <IconButton aria-label="share">
            <ShareIcon />
          </IconButton>
          <IconButton
            className={clsx(classes.expand, {
              [classes.expandOpen]: expanded
            })}
            onClick={handleExpandClick}
            aria-expanded={expanded}
            aria-label="show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        </CardActions>
        <CardCollapsible expanded={expanded} />
      </Card>
    </Grid>
  );
};

function closestDate(dateStrings) {
  const currentDate = Date.now();
  let date = -1;

  if (dateStrings.length) {
    let futureDateExists = dateStrings.some(
      (d: string) => parseInt(d) > currentDate
    );
    if (futureDateExists) {
      for (let i = 0; i < dateStrings.length; i++) {
        const dateItem = parseInt(dateStrings[i]);
        if (dateItem > currentDate) {
          date = dateItem;
          break;
        }
      }
    } else {
      for (let i = dateStrings.length - 1; i >= 0; i--) {
        const dateItem = parseInt(dateStrings[i]);
        if (dateItem <= currentDate) {
          date = dateItem;
          break;
        }
      }
    }
  }

  return date;
}

export default TourCard;
