import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import { TourFragment } from "../home/TourCards";
import { makeStyles } from "@material-ui/core/styles";
import Title from "./Title";
import QuickFacts from "./QuickFacts";
import TourGuides from "./TourGuides";
import About from "./About";
import Images from "./Images";
import Map from "./Map";
import Reviews from "./Reviews";
import Prompt from "./Prompt";

interface Props {
  slug: string | string[];
}

const TOUR = gql`
  query TOUR($slug: String!) {
    getTour(slug: $slug) {
      ...TourFragment
    }
  }
  ${TourFragment}
`;

const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: "100%",
    margin: "auto",
    [theme.breakpoints.up("md")]: {
      maxWidth: 345
    }
  },
  infoSection: {
    display: "flex"
  },
  factsAndGuides: {
    display: "flex"
  }
}));

const Tour: React.FC<Props> = ({ slug }) => {
  const { loading, error, data } = useQuery(TOUR, { variables: { slug } });
  const classes = useStyles();

  const {
    name,
    price,
    createdAt,
    summary,
    imageCover,
    difficulty,
    duration,
    startDates,
    startLocation,
    description,
    locations,
    maxGroupSize,
    rating,
    ratingsQuantity,
    ratingsAverage
  } = data.getTour;

  console.log("Toud data = ", data);

  if (loading) return <p>Loading ...</p>;

  return (
    <main className={classes.root}>
      <Title imageCover={imageCover} />
      <div className={classes.infoSection}>
        <div className={classes.factsAndGuides}>
          <QuickFacts />
          <TourGuides />
        </div>
        <About />
      </div>
      <Images />
      <Map />
      <Reviews />
      <Prompt />
    </main>
  );
};

export default Tour;
