import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import PlaceIcon from "@material-ui/icons/Place";
import CalendarIcon from "@material-ui/icons/CalendarToday";
import FlagIcon from "@material-ui/icons/EmojiFlagsRounded";
import PeopleIcon from "@material-ui/icons/PeopleOutlineRounded";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import TourCard from "./TourCard";

interface Props {
  date: string;
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  }
}));

const icons = {
  place: <PlaceIcon />,
  date: <CalendarIcon />,
  stops: <FlagIcon />,
  people: <PeopleIcon />
};

const SpotlightItem = ({ icon, text }) => {
  return (
    <>
      <div className="icon-container">{icon}</div>
      <div className="info-container">{text}</div>
    </>
  );
};

const Spotlights: React.FC<Props> = ({ date }) => {
  const classes = useStyles();

  const infoContent = {
    place: {
      icon: <PlaceIcon />,
      text: "place"
    },
    date: {
      icon: <CalendarIcon />,
      text: date
    },
    stops: {
      icon: <FlagIcon />,
      text: "stops"
    },
    people: {
      icon: <PeopleIcon />,
      text: "people"
    }
  };

  const spotLights = [
    <Grid key={1} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem {...infoContent.place} />
    </Grid>,
    <Grid key={2} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem {...infoContent.date} />
    </Grid>,
    <Grid key={3} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem {...infoContent.stops} />
    </Grid>,
    <Grid key={4} item xs={6} /*md={6} lg={4}*/>
      <SpotlightItem {...infoContent.people} />
    </Grid>
  ];

  return (
    <Grid container spacing={1}>
      {spotLights}
    </Grid>
  );
};

export default Spotlights;
